import React, { Component } from "react";

import { Header, Footer, Content } from "../../components";

import "./style.css";

export default class Home extends Component {
  constructor() {
    super();

    this.state = {
      people: "",
      tempat: "",
      tanggal: ""
    };
  }

  componentDidMount() {
    this.setState({
      people: "Budi",
      tempat: "Pacific Place",
      tanggal: "19 November 2018"
    });
  }

  handleClickHeader() {
    this.setState({
      people: "Andi"
    });
  }

  handleClickContent() {
    this.setState({
      tempat: "Kota Tua",
      tanggal: "20 November 2019"
    });
  }

  render() {
    let { people, tempat, tanggal } = this.state;

    return (
      <div>
        <Header people={people} onClick={() => this.handleClickHeader()} />

        <Content
          location={tempat}
          date={tanggal}
          onClick={() => this.handleClickContent()}
        />

        <Footer />
      </div>
    );
  }
}
